mapper.select:
SELECT 
FROM tool_usage_stats
WHERE tool_id = #{toolId} AND memory_id = #{memoryId}

SELECT 
FROM tool_usage_stats
WHERE memory_id = #{memoryId}
ORDER BY proficiency_level DESC, total_uses DESC
LIMIT #{limit}

mapper.update:
UPDATE tool_usage_stats SET total_uses = total_uses + 1, successful_uses = successful_uses + CASE WHEN #{success} THEN 1 ELSE 0 END, failed_uses = failed_uses + CASE WHEN #{success} THEN 0 ELSE 1 END, last_used_at = CURRENT_TIMESTAMP,
last_success_at = CURRENT_TIMESTAMP,
updated_at = CURRENT_TIMESTAMP WHERE tool_id = #{toolId} AND memory_id = #{memoryId}
